package LCE;

import java.util.ArrayList;
import java.util.List;

public class App {

    public static void main(String[] args) {
        App main = new App();
        main.run();
    }

    public void run() {
        Extractor extractor = new Extractor();
        GitLoader gitLoader = new GitLoader();

        extractor.run();

        List<String> result = extractor.extract();
        List<String[]> preprocessed = preprocess(result);

        gitLoader.set_result_dir("D:\\repository_d\\LCE\\result");

        for (String[] line : preprocessed) {
            // System.out.println("[debug] > " + Arrays.toString(line));
            gitLoader.config(line[2], line[0], line[1]);
            gitLoader.run();
        }
    }

    private List<String[]> preprocess(List<String> result) {
        List<String[]> result_split = new ArrayList<>();
        try {
            for (String line : result) {
                // System.out.println("[debug] line : " + line);
                String[] line_split = line.split(",");
                String[] selection = new String[] { line_split[0], line_split[2], line_split[4] };
                result_split.add(selection);
            }
        } catch (Exception e) {
            System.out.println("[error] > " + e.getMessage());
        }
        return result_split;
    }

}
